ui.css._content:
:root {
  --active-color: lightblue;
}

html, body {
  height: 100%;
  -webkit-user-select: none;
}

circle.con {
  fill: lightgray;
}

line.con {
  stroke: lightgray;
  stroke-width: 3px;
}

line.cona {
  stroke: lightgray;
  stroke-width: 1px;
  stroke-dasharray: 3;
}

text.label {
  font-size: 24px;
  font-family: system-ui;
  font-weight: bold;
  opacity: 0.1;
}

.ghost {
  background-color:blue;
}

body {
  flex-flow: column;
  display: flex;
  margin: 0;
}

.loaded {
  xanimation-name: reveal;
  animation-duration: 0.3s;
  animation-timing-function: ease-in;
}

.box {
  xpadding-left: 10px;
}

.content {
  display: inline-block;
}

.content:before {
  content: "";
  display: inline-block;
  vertical-align: middle;
  height: 100%;
}

textarea {
  display: block;
}

.markdown {
  -webkit-user-select: text;
  cursor: text;
}

.editor {
  position: relative;
  vertical-align: middle;
  display: inline-block;
  background-color: lightgray;
  margin-top: 6px;
  min-height: 200px;
  min-width: 200px;
  z-index: 0;
  cursor: default;
}

.editor.output {
  background-color: gray;
}

.editor.mode_edit.visible {
  display: inline-block;
}

.editor .sec_prompt-title {
  margin: 6px;
}

.editor .sec_prompt-tag {
  position: absolute;
  right: 6px;
  top: 6px;
  padding: 3px 12px 3px 12px;
  background-color: darkcyan;
  border-top-left-radius: 12px;
  color: white;
  display: none;
}

.editor.active .sec_prompt-tag {
  display: revert;
}

.editor.selected .sec_prompt-tag {
  display: revert;
}

.editor .sec_textarea textarea {
  resize: none;
  position: absolute;
  bottom: 6px;
  top: 18px;
  left: 6px;
  right: 6px;
}

.editor form {
  display: block;
}

.editor.active {
  background-color: var(--active-color);
  z-index: 1;
}

.editor.selected {
  background-color: aquamarine;
}

.rows {
  margin-left: 30px;
  display: inline-block;
  vertical-align: middle;
}

.view > .rows {
  margin-top: 100px;
  margin-bottom: 300px;
  margin-right: 600px;
}

.view > .sec_libs {
  padding: 20px;
  padding-top: 35px;
  background-color: lightsteelblue;
}

button {
  display: inline-block;
}

.sec_head {
  flex: 1 0 auto;
  box-sizing: border-box;
  margin: 0;
  padding: 12px;
}

.sec_scroll-view {
  flex: 1 1 auto;
  height: 100%;
  overflow: scroll;
  box-sizing: border-box;
  background-color: aliceblue;
  cursor: move;
}

.view {
  min-inline-size: max-content;
  position: relative;
}

.sec_background {
  position: absolute;
}

body.edit .ui_button-act:not(.visible) .act_mode-toggle {
  display: none;
}

.sec_edit-actions {
  flex: 0 1 auto;
  box-sizing: border-box;
  padding: 10px;
}

.sec_edit-preview {
  flex: 1 1 auto;
  width: 100%;
  height: 100%;
  box-sizing: border-box;
  padding: 10px;
  overflow: scroll;
  font-size: initial;
}

.sec_edit-text {
  flex: 1 1 auto;
  width: 100%;
  height: 100%;
  box-sizing: border-box;
  padding: 10px;
  overflow: scroll;
}

.sec_edit-text form {
  width: 100%;
  height: 100%;
}

textarea.sec_prompt-text {
  resize: none;
  min-height:60px;
}

.sec_edit-text form textarea {
  width: 100%;
  height: 100%;
  resize: none;
}

button.act_transform .sec_prompt-value {
  display: none;
}

button.act_transform:hover .sec_prompt-value {
  font-family: var(--font);
  font-size: smaller;
  display: block;
  position: absolute;
  background-color: white;
  bottom: 32px;
  left: 16px; 
  width: 245px;
  overflow: clip;
  text-align: left;
  color: gray;
}

.sec_output {
  padding: 6px;
  background-color: white;
  overflow: scroll;
  xborder: 1px solid black;
  position: absolute;
  top: 26px;
  left: 6px;
  right: 6px;
  bottom: 6px;
  -webkit-user-select: auto;
}

.sec_inner {
  height: 100%;
}

.ui_button-act {
  position: absolute;
  background-color: black;
  text-align: center;
}

.ui_button-act > a {
  color: white;
  display: block;
  text-decoration: none;
  -webkit-user-select: none;
  font-family: var(--font);
  font-size: 21px;
  width: 24px;
  height: 24px;
  line-height: 22px;
}

.view .sec_libs .editor .sec_actions-left,
.view .sec_libs .editor .sec_actions-bottom,
.view .sec_libs .editor .sec_actions-right,
.view .sec_libs .editor .sec_prompt-tag,
.view .sec_libs .editor .ui_dragger-resizer {
    display: none !important;
}

.view .sec_libs .editor {
  width: 100px !important;
  height: 100px !important;
  min-width: revert !important;
  min-height: revert !important;
  background-color: lightslategrey;
}

.view .sec_libs .box {
  display: inline-block;
}

.view > .rows > .box > .content > .editor.active .sec_actions-left {
  display: none !important;
}

.sec_actions-left {
  left: -24px;
  top: 50%;
  transform: translateY(-50%);
  display: none;
  border-top-left-radius: 14px;
  border-bottom-left-radius: 14px;
}

.sec_actions-right {
  right: -24px;
  top: 50%;
  transform: translateY(-50%);
  display: none;
  border-top-right-radius: 14px;
  border-bottom-right-radius: 14px;
}

.sec_actions-top {
  top: -20px;
  margin-left: 50%;
  transform: translateX(-50%);
  display: none;
  border-radius: 4px;
}

.sec_actions-bottom {
  bottom: -20px;
  margin-left: 50%;
  transform: translateX(-50%);
  display: none;
  border-radius: 4px;
}

.editor .sec_output-document.markdown {
  margin: 0;
  padding: 0;
} 

.active .ui_button-act {
  display: block;
}

.active .ui_button-act .sec_actions-tools {
  display: none;
}

.mode_edit {
  display: none;
}

.active .ui_button-act.mode_edit .sec_actions-tools {
  display: block;
}

.editor.highlighted {
  transform: scale(1);
  animation: pulse 2s infinite;
}

.editor.transforming {
  background: rgb(222, 84, 72);
  xborder-radius: 50%;
  height: 30px;
  width: 30px;
  box-shadow: 0 0 0 0 rgba(222, 84, 72, 1);
  transform: scale(1);
  animation: pulse 2s infinite;
}

.active.transforming .ui_dragger-resizer {
  display: none;
}

.ui_dragger-resizer {
  display: none;
}

.active .ui_dragger-resizer {
  position: absolute;
  right: 0;
  bottom: 0;
  background-color: var(--active-color);
  width: 30px;
  height: 30px;
  border-top-left-radius: 30px;
  display: inline-block;
}

.mode_edit.visible .sec_actions-tools {
  box-shadow: 10px 11px 24px 1px rgba(0,0,0,0.7);
  display: block;
  position: absolute;
  top: 0px;
  left: 0px;
  width: 250px;
  background-color: white;
  border: 5px solid black;
  border-radius: 10px;
  border-top-left-radius: 0px;
  padding: 10px 16px 16px 16px;
  text-align: left;
}

#sec_scroll-tools {
  position: absolute;
  top: 100px;
  right: 10px;
  z-index: 999;
}

#sec_scroll-tools .ui_button {
  display: block;
  margin: 0;
  border: unset;
  background-color: unset;
}

.sec_actions-top .sec_actions-tools {
  left: -50px !important;
  top: -6px !important;
}

.sec_actions-bottom .sec_actions-tools {
  left: -50px !important;
  top: -20px !important;
}

.sec_actions-left .sec_actions-tools {
  transform: translateY(-50%);
}

.sec_actions-right .sec_actions-tools {
  left: -20px !important;
  transform: translateY(-50%);
}

.mode_filter .markdown > * {
  display: none;
}

.mode_filter .markdown *:has(.filter_word) {
  display: revert;
}

.mode_filter .filter_word {
  background-color: yellow !important;
}

#ui_popup {
  border: 5px solid black;
  position: fixed;
  z-index: 2;
  display: none;
  top: 0;
  right: 0;
  height: 100%;
  width:30%;
  box-sizing: border-box;
  background-color: white;
  box-shadow: -7px 0px 60px -3px rgb(0 0 0 / 50%);
}

#ui_popup.visible {
  animation-name: slidein;
  animation-duration: 0.2s;
  display: block;
}

#ui_popup-background {
  width: 100%;
  height: 100%;
  position: fixed;
  z-index: 1;
  background-color: black;
  opacity: 0.25;
}

@keyframes pulse {
    0% {
        transform: scale(0.95);
        box-shadow: 0 0 0 0 rgba(222, 84, 72, 0.7);
    }

    70% {
        transform: scale(1);
        box-shadow: 0 0 0 15px rgba(222, 84, 72, 0);
    }

    100% {
        transform: scale(0.95);
        box-shadow: 0 0 0 0 rgba(222, 84, 72, 0);
    }
}  

@keyframes reveal {
  from {
    opacity: 0;
  }

  to {
    opacity: 1;
  }
}

@keyframes reflow {
  from {
    opacity: 0.5;
    transform-origin: center;
    transform: scale(1.1, 1.1);
  }

  to {
    opacity: 1;
    transform-origin: center;
    transform: scale(1, 1);
  }
}

@keyframes slidein {
  from {
    opacity: 0;
    right: -30%;
  }

  to {
    opacity: 1;
    right: 0;
  }
}

