}
*/

.sec_note-fulleditor {
  max-width: 600px;
}

.CodeMirror-widget img {
  display: inline-block;
  max-width: 100%;
}

.CodeMirror-code {
  font-size: {{_.body_size}}px;
  color: {{_.body_color}};
}

pre.CodeMirror-line {
  font-family: {{_.body_name}};
} 

.cm-header.cm-1 {
  font-size: {{_.h1_size}}px;
  font-family: {{_.h1_name}};
  color: {{_.h1_color}};
  font-weight: {{or _.h1_bold "normal"}};
}

.cm-header.cm-2 {
  font-size: {{_.h2_size}}px;
  font-family: {{_.h2_name}};
  color: {{_.h2_color}};
  font-weight: {{or _.h2_bold "normal"}};
}

.cm-header.cm-3 {
  font-size: {{_.h3_size}}px;
  font-family: {{_.h3_name}};
  color: {{_.h3_color}};
  font-weight: {{or _.h3_bold "normal"}};
}

.cm-header.cm-4 {
  font-size: {{_.h4_size}}px;
  font-family: {{_.h4_name}};
  color: {{_.h4_color}};
}

.cm-bold {
  font-weight: {{or _.strong_bold "normal"}};
  color: {{_.strong_color}};
}

.cm-italic {
  font-weight: {{or _.strong_bold "normal"}};
  font-style: italic;
  color: {{_.em_color}};
}

.cm-underlined {
  font-weight: normal;
  text-decoration: underline;
}

.cm-strikethrough {
  text-decoration: line-through;
}

.cm-list {
}

.cm-list.cm-2 {
}

.cm-task-open {
  font-family: monospace;
  color: -webkit-link;
}

.cm-task-closed {
  font-family: monospace;
  color: -webkit-link;
}

.cm-link {
  text-decoration: underline;
  color: -webkit-link;
}

.cm-link-text {
  color: -webkit-link;
}

.cm-link-wikiname {
