(function (res, writer, ctx) {
  let query = ctx.getQueryProperties();

  if (query && query.article_file) {
    get_file(query.article_file, function(txt) {
      let article = JSON.parse(txt);

      console.log(article);

      let opts = {
        headingStyle:'atx'
      };

      let td = new TurndownService(opts);
      let md = td.turndown(article.content);

      let path = sessionStorage.getItem('__CURRENT_CONTENT_PATH');
      if (path) {
        saveArticleFragment(ctx, path, md, function() {
          let pref = ctx.getConfigProperty('APP_PREFIX');
          if (pref) path = pref + path;
          ctx.forwardRequest(path);
          writer.end();
        });
      }
      else {
        writer.start('text/plain');
        writer.write('no board selected?');
        writer.end();
      }
    });
  }
  else {
    writer.start('text/plain');
    writer.write('no parameters?');
    writer.end();
  }
});
